import React from "react";
import ReactImageMagnify from 'react-image-magnify';
//usage guide: https://www.npmjs.com/package/react-image-magnify
var Modal = require('react-bootstrap-modal')
import { useStoreContext } from "../../utils/GlobalState";
import "./style.css";
import Tester from './product1.jpg';
import Texture from '../Images/45-degree-fabric-light.png';
// https://www.transparenttextures.com/

var cardStyle = {
    backgroundImage: `url(${Texture})`
};

function Product() {
  const [store] = useStoreContext();
  return (
        <div id="productCard" style={cardStyle}>
            <p>Name: </p>
            <img id="productPic" src={Tester}/>
            <Modal
          show={this.state.open}
          onHide={closeModal}
          aria-labelledby="ModalHeader"
        >
          <Modal.Header closeButton>
            <Modal.Title id='ModalHeader'>A Title Goes here</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Some Content here</p>
          </Modal.Body>
          <Modal.Footer>
            // If you don't have anything fancy to do you can use
            // the convenient `Dismiss` component, it will
            // trigger `onHide` when clicked
            <Modal.Dismiss className='btn btn-default'>Cancel</Modal.Dismiss>
 
            // Or you can create your own dismiss buttons
            <button className='btn btn-primary' onClick={saveAndClose}>
              Save
            </button>
          </Modal.Footer>
        </Modal>
            {/* <ReactImageMagnify {...{
                smallImage: {
                    alt: 'Stapler!!!!!!!!!!',
                    isFluidWidth: true,
                    src: Tester
                },
                largeImage: {
                    src: Tester,
                    width: 1200,
                    height: 1800
                }
            }} /> */}
            <p>Price:</p>
            <p>Description: </p>
            <p>In stock: </p>
        </div>
    );
}

export default Product;
