import React, { useState, useEffect } from "react";
import { Col, Row, Container } from "../components/Grid";
import '../utils/API';
import API from "../utils/API";

function Testing() {
    const [inventoryList, setinventoryList] = useState([]);
    const [newItemName, setNewItemName] = useState("");
    const [newPrice, setNewPrice] = useState("");
    const [newDescription, setnewDescription] = useState("");
    const [newQuantity, setNewQuantity] = useState("");

    useEffect(() => {
        const getInventory = async () => {
            let inventoryItems = await API.getAllItems()
            console.log(inventoryItems)
            console.log(inventoryItems)
            setinventoryList(inventoryItems)
        }
        getInventory();
    }, [])

    // When the form is submitted, we validate there's an email and password entered
    function submitThisForm (event) {
        event.preventDefault()

        var userData = {
            userName: newUsername,
            password: newPassword
        }
        API.saveUser(userData);
    }

    function handleNameChange(event) {
        const name = event.target.value;
        setNewItemName(name)
    }

    function handlePriceChange(event) {
        const price = event.target.value;
        setNewPrice(price)
    }

    function handleQuantityChange(event) {
        const quantity = event.target.value;
        setNewQuantity(quantity)
    }

    function handleDescriptionChange(event) {
        const description = event.target.value;
        setnewDescription(description)
    }


    return (
        <Container fluid>
            <form className="login" onSubmit={submitThisForm}>
                <div className="form-group">
                    <input type="text" onChange={handleNameChange} className="form-control" placeholder="Item Name" />
                </div>
                <div className="form-group">
                    <input type="password" onChange={handlePriceChange} className="form-control" placeholder="Item Price" />
                </div>
                <div className="form-group">
                    <input type="password" onChange={handleQuantityChange} className="form-control" placeholder="# of items" />
                </div>
                <div className="form-group">
                    <input type="password" onChange={handleDescriptionChange} className="form-control" placeholder="Description" />
                </div>
                <button type="submit" className="btn btn-default  green darken-3">Submit that bad mutha-shut-yo-mouth</button>
            </form>

            {/* <button onClick={API.getAllUsers}>Get Users</button> */}
            <div className="container">
                <div id="tableWrapper" className="row">
                    <table className="table table-striped table-hover">
                        <thead>
                            <tr id="tableHeader" className="bg-warning">
                                <th scope="col">
                                    Name
                    </th>
                            </tr>
                        </thead>
                        <tbody>
                            {inventoryList?.map((result, index) => {
                                return (
                                    <tr key={index}>
                                        <td>
                                            {result.userName}
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
        </Container>
    );
};

export default Testing;
