import React, { useState, useEffect } from "react";
import { useStoreContext } from "../../utils/GlobalState";
import ReactImageMagnify from 'react-image-magnify';
//usage guide: https://www.npmjs.com/package/react-image-magnify
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css"
import Slider from "react-slick";
import Tester1 from './product1.jpg';
import Tester2 from './product2.jpg';
import Tester3 from './product3.jpg';
import Tester4 from './product4.jpg';
import Texture from '../Images/45-degree-fabric-light.png';
// https://www.transparenttextures.com/
import "./style.css";

//API stuff
import API from "../../utils/API";
import {Link} from 'react-router-dom';
//API stuff done

const cardStyle = {
    backgroundImage: `url(${Texture})`
};

export default function v2Product() {
    const [store] = useStoreContext();
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    const [inventoryList, setinventoryList] = useState([]);
    const [newItemName, setNewItemName] = useState("");
    const [newPrice, setNewPrice] = useState("");
    const [newDescription, setnewDescription] = useState("");
    const [newQuantity, setNewQuantity] = useState("");

    useEffect(() => {
        const getInventory = async () => {
            let inventoryItems = await API.getAllItems()
            console.log(inventoryItems)
            setinventoryList(inventoryItems)
        }
        getInventory();
    }, [])

    function submitThisForm (event) {
        event.preventDefault()

        var itemData = {
            itemName: newItemName,
            price: newPrice,
            description: newDescription,
            quantity: newQuantity
        }
        console.log(itemData)
        API.saveItem(itemData);
    }

    function handleNameChange(event) {
        const name = event.target.value;
        console.log(name);
        setNewItemName(name)
    }

    function handlePriceChange(event) {
        const price = event.target.value;
        setNewPrice(price)
    }

    function handleQuantityChange(event) {
        const quantity = event.target.value;
        setNewQuantity(quantity)
    }

    function handleDescriptionChange(event) {
        const description = event.target.value;
        setnewDescription(description)
}
  return (
        <div>
            {inventoryList?.map((result) => {
                                return (<div id="productCard" style={cardStyle} key={result._id}>
                                    <Slider {...settings}>
                <div>
                    <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester1,
                            enlargedImagePosition: 'over',
                        },
                        largeImage: {
                            src: Tester1,
                            width: 800,
                            height: 800
                        },
                        enlargedImagePosition: 'over',
                        enlargedImageContainerDimensions: {width: '100%', height: '100%'}
                    }} />
                </div>
                <div>
                            <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester2,
                            enlargedImagePosition: 'over',
                        },
                        largeImage: {
                            src: Tester2,
                            width: 800,
                            height: 800
                        },
                        enlargedImagePosition: 'over',
                        enlargedImageContainerDimensions: {width: '100%', height: '100%'}
                    }} />
                </div>
                <div>
                            <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester3,
                            enlargedImagePosition: 'over',
                        },
                        largeImage: {
                            src: Tester3,
                            width: 1200,
                            height: 1800
                        },
                        enlargedImagePosition: 'over',
                        enlargedImageContainerDimensions: {width: '100%', height: '100%'}
                    }} />
                </div>
                <div>
                            <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester4,
                            enlargedImagePosition: 'over',
                        },
                        largeImage: {
                            src: Tester4,
                            width: 1200,
                            height: 1800
                        },
                        enlargedImagePosition: 'over',
                        enlargedImageContainerDimensions: {width: '100%', height: '100%'}
                    }} />
                </div>
            </Slider>
            <br/>
                <p>Name: {result.itemName}
                </p>
                <p>Price: {result.price}</p>
                <p>Description: {result.description}</p>
                <p>In stock: {result.quantity}</p>
                <div>
                    Add to cart: <input></input>
                    <button onClick={updateCartItemSellQuantity(result._id)} >Update number of things</button>
                    <input onChange={handleQuantityChange} type="number" placeholder={result.sellQuantity} id="quantity" name="quantity" min={1} max={5} />
                </div>
            </div>
                );
            })}
        </div>
    );
}
