import React, { useState, useEffect } from "react";
import "./style.css";
import Texture from '../Images/45-degree-fabric-light.png';
import CartItem from '../CartItem';
import API from "../../utils/API";

const checkoutStyle = {
  backgroundImage: `url(${Texture})`
};

export default function Checkout() {
  const [cartList, setCart] = useState([]);
  const [currentCartId, setCurrentCartId] = useState([]);
  useEffect(() => {
    const getCart = async () => {
        let cartItems = await API.getAllCartItems()
        console.log(cartItems)
        setCart(cartItems)
    }
    getCart();
  }, [])

  const clearCart = () => {
    return event => {
      event.preventDefault();
      let cartItems = API.getAllCartItems()
      console.log(cartItems)
      cartItems.forEach(item => {
        API.deleteCartItem(item._id)
      });
      location.reload();
    }
  }


  // function clearCart = itemId => {
  //   {cartList?.map((result._id) => { 
  //       return event => {
  //           event.preventDefault();
  //           API.deleteCartItem(itemId);
  //           location.reload();
  //     }
  // })}}

  return( <div id="checkout" style={checkoutStyle}>
          <CartItem/>
          <a onclick={clearCart} class="totalInfo text-muted">Clear cart</a>
          <hr/>
          <p class="totalInfo">Subtotal: </p>
          <p class="totalInfo">Sales tax: </p>
          <p class="totalInfo">Total: </p> 
      <button
        className="btn btn-primary"
      >Submit order</button>
    </div>
    );
};
