import React, { useState, useEffect } from "react";
import { Col, Row, Container } from "../components/Grid";
import "../utils/API"
import API from "../utils/API";





function Testing() {
    // let newUsername = document.querySelector("#username-input");
    // let newPassword = document.querySelector("#password-input");

    const [userList, setUserList] = useState([]);
    const [newUsername, setUsername] = useState("");
    const [newPassword, setPassword] = useState("");

    useEffect(() => {
        const getUsers = async () => {
            const users = await API.getAllUsers()
            setUserList(users)
        }
        getUsers();
    }, [])

    // When the form is submitted, we validate there's an email and password entered
    function submitThisForm (event) {
        event.preventDefault();
        setUsername(document.querySelector("#username-input").val)
        setPassword(document.querySelector("#password-input").val)

        var userData = {
            userName: newUsername,
            password: newPassword
        }
        console.log("USERDATA IS AS FOLLOWS ")
        console.log(userData)
        API.saveUser(userData);
        // newUsername.val("");
        // newPassword.val("");
    }

    return (
        <Container fluid>
            <form className="login" onSubmit={submitThisForm}>
                <div className="form-group">
                    <input type="text" className="form-control" id="username-input" placeholder="Username" />
                </div>
                <div className="form-group">
                    <input type="password" className="form-control" id="password-input" placeholder="Password" />
                </div>
                <button type="submit" className="btn btn-default  green darken-3">Login</button>
            </form>

            {/* <button onClick={API.getAllUsers}>Get Users</button> */}
            <div className="container">
                <div id="tableWrapper" className="row">
                    <table className="table table-striped table-hover">
                        <thead>
                            <tr id="tableHeader" className="bg-warning">
                                <th scope="col">
                                    Name
                    </th>
                            </tr>
                        </thead>
                        <tbody>
                            {userList?.map((result, index) => {
                                return (
                                    <tr key={index}>
                                        <td>
                                            {result.userName}
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
        </Container>
    );
};

export default Testing;
