import "./style.css";
import CheckoutItem from '../CheckoutItem';
import Texture from '../Images/45-degree-fabric-light.png';

const checkoutStyle = {
    backgroundImage: `url(${Texture})`
  };

function ModalCart() {
  const [store] = useStoreContext();
  
  return (<div id="modalCart" style={checkoutStyle}>
  <CheckoutItem/>
  <CheckoutItem/>
  <CheckoutItem/>
  <CheckoutItem/>
  <CheckoutItem/>
  <CheckoutItem/>
  <CheckoutItem/>
  <hr/>
  <p class="totalInfo">Subtotal: </p>
  <p class="totalInfo">Sales tax: </p>
  <p class="totalInfo">Total: </p> 
<button
className="btn btn-primary" href="/completesale"
>Check out</button>
</div>
    );
}

export default ModalCart;
