import React from "react";
import ReactImageMagnify from 'react-image-magnify';
//usage guide: https://www.npmjs.com/package/react-image-magnify
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css"
import Slider from "react-slick";;
import { useStoreContext } from "../../utils/GlobalState";
import "./style.css";
import Tester1 from './product1.jpg';
import Tester2 from './product2.jpg';
import Tester3 from './product3.jpg';
import Tester4 from './product4.jpg';
import Texture from '../Images/45-degree-fabric-light.png';
// https://www.transparenttextures.com/

const cardStyle = {
    backgroundImage: `url(${Texture})`
};

export default function Product() {
  const [store] = useStoreContext();
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (
        <div id="productCard" style={cardStyle}>
            <p>Name: </p>
            <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester1,
                            
                        },
                        largeImage: {
                            src: Tester1,
                            width: 1200,
                            height: 1800,
                        },
                        enlargedImagePosition: 'over'
                    }} />
            <Slider {...settings}>
                <div>
                            <ReactImageMagnify {...{
                        smallImage: {
                            alt: 'Stapler!!!!!!!!!!',
                            isFluidWidth: true,
                            src: Tester1,
                            enlargedImagePosition: 'over',
                            enlargedImageContainerDimensions: {width: '200%', height: '200%'}
                        },
                        largeImage: {
                            src: Tester2,
                            width: 1200,
                            height: 1800
                        }
                    }} />
                    <h3>1</h3>
                </div>
                <div>
                <img id="productPic" src={Tester2}/>
                    <h3>2</h3>
                </div>
                <div>
                <img id="productPic" src={Tester3}/>
                    <h3>3</h3>
                </div>
                <div>
                    <img id="productPic" src={Tester4}/>
                    <h3>4</h3>
                </div>
                </Slider>
            
            <p>Price:</p>
            <p>Description: </p>
            <p>In stock: </p>
        </div>
    );
}
