import React from "react";

import { useToasts } from "react-toast-notifications";

export const Toaster = ({ content }) => {
  const { addToast } = useToasts();
  return (
    <button
      onClick={() =>
        addToast(content, {
          appearance: "success",
          autoDismiss: true,
        })
      }
    >
      Add Toast
    </button>
  );
};

export default Toaster;
